import { Component, OnInit, OnDestroy } from '@angular/core';
import { Router, ActivatedRoute } from "@angular/router";
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { TestService } from 'service/test.service';

import { ApiResponse } from 'model/apiResponse';
import { PatternSocketModel } from 'model/pattern';
import { LapModel } from 'model/test';
import Swal from 'sweetalert2'


@Component({
  selector: 'app-view',
  templateUrl: './view.component.html',
  styleUrls: ['./view.component.css']
})
export class TestViewComponent implements OnInit, OnDestroy {

  private routeSub: any;
  connection;
  lapConnection;
  timeConnection;
  timeStopConnection;
  test_id;
  isLoading = true;
  isPatternLoading = true;
  info: any;
  labs = [];
  pattern: PatternSocketModel;
  startTime;
  interval;
  time = [0, 0, 0, 0];
  isStart = false;
  constructor(private route: ActivatedRoute,
    private formBuilder: FormBuilder,
    private router: Router,
    private testService: TestService) { }

  ngOnInit() {
    

    this.routeSub = this.route.params.subscribe(params => {
      this.test_id = params['id'];
      this.testService.getTest(this.test_id).subscribe(res => {
        let result: ApiResponse;
        result = res as ApiResponse;
        console.log(result)
        this.isLoading = false;
        if (result.successful) {
          this.info = result.data;
          this.getPattern();
          this.startTest();
          this.getLab();
          this.getStartTime();
          this.getStopTime();
        }else{
          this.isPatternLoading = false;
          alert("การสร้างแบบทดสอบผิดพลาด");
          this.router.navigate(['/test']);
        }

      })
    })


  }

  ngOnDestroy() {
    if (this.routeSub) {
      this.routeSub.unsubscribe();
    }
    if (this.connection) {
      this.connection.unsubscribe();
    }
    if (this.lapConnection) {
      this.lapConnection.unsubscribe();
    }
  }

  startTest() {
    this.testService.startTest(this.info);
  }

  getPattern() {
    this.connection = this.testService.getPattern().subscribe(res => {
      console.log("pattern");
      console.log(res);
      this.pattern = res as PatternSocketModel;
      this.isPatternLoading = false;
    })
  }

  getLab() {
    this.lapConnection = this.testService.getLap().subscribe(res => {
      console.log("lab");
      console.log(res);
      this.labs.push(res as LapModel);
      this.beep();
    })
  }

  clickStart() {
    this.testService.start();
  }

  clickStop() {
    this.testService.stop();
  }

  getStartTime() {
    this.timeConnection = this.testService.getStartTime().subscribe(res => {
      this.isStart = res as boolean;
      if(this.isStart){
        this.startTimer();
      }      
    })
  }

  getStopTime() {
    this.timeStopConnection = this.testService.getStopTime().subscribe(res => {
      console.log(res);
      clearInterval(this.interval);
      this.time = res['time'];
      this.pattern.text = res['text'];
    })
  }

  startTimer() {
    this.interval = setInterval(() => {
      if (this.time[2] <= 100) {
        this.time[2]++;
        this.time[3] = Math.floor(Math.floor(Math.random() * 9) + 1);
      } else {
        this.time[1]++;
        this.time[2] = 0;
        this.time[3] = 0;
        if (this.time[1] > 60) {
          this.time[0]++;
          this.time[1] = 0;
        }
      }

    }, 10)
  }



  pauseTimer() {
    clearInterval(this.interval);
  }

  resetTimer() {
    Swal.fire({
      title: 'Are you sure?',
      text: "You won't be able to revert this!",
      type: 'warning',
      showCancelButton: true,
      confirmButtonText: 'Yes, reset it!',
      cancelButtonText: 'No, cancel!',
      reverseButtons: true
    }).then((result) => {
      if (result.value) {
        this.testService.resetTest(this.test_id).subscribe(res => {
          let result : ApiResponse;
          result = res as ApiResponse;
          console.log(result);
          if(result.successful){
            Swal.fire(
              'Deleted!',
              'Your test has been reset.',
              'success'
            )
            this.labs = [];
          }else{
            Swal.fire(
              'Error',
              'Something error',
              'error'
            )
          }
        });
      }
    })
  }

  beep() {
    var snd = new Audio("data:audio/wav;base64,");
    snd.play();
  }

}
